mapper.select:

	select member_id,password,member_name
	from member
	where
	member_id=#{member_id}
	and password=#{password}

mapper.insert:
						
		INSERT INTO member(member_id
		, password
		, member_name
		,email
		,birth
		,phone
		)
		VALUES( #{member_id}
		, #{password}
		, #{member_name}
		, #{email}
		, #{birth}
		, #{phone}
		)
