mapper.select:

	select member_id,password,member_name
	from member
	where
	member_id=#{member_id}
	and password=#{password}


	select password
	from member
	where
	member_id=#{member_id}
	and phone=#{phone}
	and email=#{email}


	select member_id
	from member
	where
	member_name=#{member_name}
	
	and phone=#{phone}
	
	
	and email=#{email}

mapper.insert:
						
		INSERT INTO member(member_id
		, password
		, member_name
		,email
		,birth
		,phone
		)
		VALUES( #{member_id}
		, #{password}
		, #{member_name}
		, #{email}
		, #{birth}
		, #{phone}
		)
